mapper.insert:
INSERT INTO TB_GRP_CODE (GRP_CODE
                       , GRP_CODE_NAME
                       , STATUS)
                VALUES (#{grpCodeDto}
                      , #{grpCodeName}
                      , 'Y')

mapper.update:
UPDATE TB_GRP_CODE
   SET GRP_CODE = #{grpCodeDto}
     , GRP_CODE_NAME = #{grpCodeName}
 WHERE GRP_CODE = #{grpCodeDto}
   AND STATUS = 'Y'

UPDATE TB_GRP_CODE
   SET STATUS = 'N'
 WHERE GRP_CODE = #{grpCodeDto}
