mapper.insert:
INSERT INTO TB_CODE (CODE
				   , CODE_NAME
				   , SORT_NO
				   , GRP_CODE)
			 VALUES (#{code}
			 	   , #{codeName}
			 	   , #{sortNo}
			 	   , #{grpCode})

mapper.update:
UPDATE TB_CODE
   SET CODE_NAME = #{codeName}
     , SORT_NO = #{sortNo}
 WHERE CODE = #{code}
   AND GRP_CODE = #{grpCode}

UPDATE TB_CODE
SET  STATUS = 'N'
WHERE CODE = #{code}
  AND GRP_CODE = #{grpCode}
