mapper.insert:
INSERT INTO TB_GRP_CODE (GRP_CODE
					   , GRP_CODE_NAME
					   , STATUS)
				VALUES (#{grpCode}
					  , #{grpCodeName}
					  , 'Y')

mapper.update:
UPDATE TB_GRP_CODE
   SET GRP_CODE = #{grpCode}
   	 , GRP_CODE_NAME = #{grpCodeName}
 WHERE GRP_CODE = #{grpCode}
   AND STATUS = 'Y'

UPDATE TB_GRP_CODE
   SET STATUS = 'N'
 WHERE GRP_CODE = #{grpCode}
