mapper.insert:

  SELECT SEQ_TB_CHALLENGE.NEXTVAL
    FROM DUAL


INSERT INTO TB_CHALLENGE (CH_NO
                        , CH_TITLE
                        , CH_START_DAY
                        , CH_END_DAY
                        , MEMBER_ID
                        , CATEGORY_NO
                        , FILE_NO
                        , CH_CONTENT
                        , STATUS)
                VALUES (#{chNo}
                      , #{chTitle}
                      , #{chStartDay}
                      , #{chEndDay}
                      , #{memberId}
                      , #{categoryNo}
                      , #{fileNo}
                      , #{chContent}
                      , 'Y')

INSERT INTO TB_CH_DETAIL_INFO ( CH_DETAIL_INFO_NO
                              , CH_NO
                              , CATEGORY_TEMPLATE_NO
                              , CH_DETAIL_INFO_DATA
                              , CODE
                              , FILE_NO)
VALUES ( SEQ_TB_CH_DETAIL_INFO.NEXTVAL
       , #{chNo}
       , #{categoryTemplateNo}
       , #{chDetailInfoData}
       , #{code}
       , #{fileNo})

mapper.update:
UPDATE TB_CHALLENGE
   SET CH_TITLE = #{chTitle}
     , CH_START_DAY = #{chStartDay}
     , CH_END_DAY = #{chEndDay}
     , FILE_NO = #{fileNo}
     , CH_CONTENT = #{chContent}
 WHERE CH_NO = #{chNo}
   AND MEMBER_ID = #{memberId}
   AND CATEGORY_NO = #{categoryNo}
   AND STATUS = 'Y'

UPDATE TB_CHALLENGE
   SET STATUS = 'N'
 WHERE CH_NO = #{chNo}
