mapper.insert:
INSERT INTO PAYMENT (PAYMENT_NO
								  , MEMBER_ID
								  , PAYMENT_DATE
								  , PAYMENT_TYPE
								  , STATUS
								  , PAYMENT_TOTAL
								  , RECEIVE_NAME
								  , RECEIVE_PHONE
								  , POST_CODE
								  , ADDRESS1
								  , ADDRESS2
								  , DELIVERY_COMMENT
								  , PAYMENT_TOKEN
								  , REWARD_POINT)
							VALUES (#{paymentNo}
								  , #{memberId}
								  , SYSDATE
								  , #{paymentType}
								  , 'Y'
								  , #{paymentTotal}
								  , #{receiveName}
								  , #{receivePhone}
								  , #{postCode}
								  , #{receiveAddress1}
								  , #{receiveAddress2}
								  , #{deliveryComment}
								  , #{paymentToken}
								  , #{rewardPoint})

mapper.update:
UPDATE MEMBER
      SET TOTAL_PAY = TOTAL_PAY + #{paymentTotal}
 WHERE MEMBER_ID = #{memberId}
