mapper.insert:
INSERT INTO MEMBER(MEMBER_ID
								  , MEMBER_PWD
								  , MEMBER_NAME
								  , BIRTH_DATE
								  , GENDER
								  , EMAIL
								  , PHONE)
			     VALUES(#{memberId}
						 	   , #{memberPwd}
						 	   , #{memberName}
						 	   , #{birthDate}
						 	   , #{gender}
						 	   , #{email}
						 	   , #{phone})

mapper.update:
UPDATE MEMBER
        SET MEMBER_NAME = #{memberName} 
        	  , MEMBER_PWD = #{memberPwd}
              , BIRTH_DATE = #{birthDate}
              , GENDER = #{gender}
              , EMAIL = #{email}
              , PHONE = #{phone}
              , CREATED_DATE = SYSDATE
 WHERE MEMBER_ID = #{memberId}
